import React, { useEffect, useState } from 'react';
import { BASE_URL } from './config';
import "./viewdeploy.css";

const DeploymentTable = () => {
  const [deploymentData, setDeploymentData] = useState([]);
  const [rulesData, setRulesData] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchDeploymentData = async () => {
      try {
        const response = await fetch(BASE_URL + '/getDetectionImagesUploadDetails', {
          method: 'POST',
          headers: {
            'x-access-token': token
          }
        });
        const data = await response.json();

        if (response.status === 401) {
          // Token is missing or invalid, show the error message
          alert(data.message);
          // Redirect to '/login'
          window.location.href = '/login';
          return; // Stop further execution
        }

        setDeploymentData(data.filter((item) => item.dataType === 'detectionData'));
      } catch (error) {
        console.error('Error fetching deployment data:', error);
      }
    };


    const fetchRulesData = async () => {
      try {
        const response = await fetch(BASE_URL+'/getRules' , {
            method: 'POST',
            headers: {
              'x-access-token': token
            }
          }
        );
        const data = await response.json();

        setRulesData(data.data.reverse());
      } catch (error) {
        console.error('Error fetching rules data:', error);
      }
    };

    fetchDeploymentData();
    fetchRulesData();
  }, [token]);

  const getRuleNames = (ruleIds) => {
    return ruleIds.map((ruleId) => {
      const rule = rulesData.find((rule) => rule['Rule ID'] === ruleId);
      return rule ? rule['Rule Name'] : '';
    });
  };

  const downloadReport = async (requestID) => {
    try {
      const response = await fetch(BASE_URL + '/reports/getDetectionResults', {
        method: 'POST',
        headers: {
          'x-access-token': token,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ 
          testUploadID: requestID })
      });

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'report.xlsx';
      a.click();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading report:', error);
    }
  };

  const [showPopup, setShowPopup] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleClick = () => {
    setShowPopup(true);
  };

  const handleOptionSelect = (option) => {
    // Handle option selection logic here
    setSelectedOption(option);
    console.log(option);
  };

  const handleClosePopup = () => {
    setSelectedOption(null);
    setShowPopup(false);
  };



  const [selectedRuleNames, setSelectedRuleNames] = useState([]);
  const [filterOption, setFilterOption] = useState('Any');

  const handleRuleNameSelect = (ruleName) => {
    if (selectedRuleNames.includes(ruleName)) {
      setSelectedRuleNames(selectedRuleNames.filter((name) => name !== ruleName));
    } else {
      setSelectedRuleNames([...selectedRuleNames, ruleName]);
    }
  };

  const handleFilterOptionSelect = (option) => {
    setFilterOption(option);
  };

  const applyFilter = (item) => {
    if (filterOption === 'Any') {
      return selectedRuleNames.some((name) => item.rulesList.includes(name));
    } else if (filterOption === 'All') {
      return selectedRuleNames.every((name) => item.rulesList.includes(name));
    }
    return true;
  };

  return (
    <div>
      <h2>Deployment Status</h2>
      <div className="filter-options">
        <span>Filter by Rule Names:</span>
        <select
          value={filterOption}
          onChange={(e) => handleFilterOptionSelect(e.target.value)}
        >
          <option value="Any">Any</option>
          <option value="All">All</option>
          {getUniqueRuleNames().map((ruleName) => (
            <option key={ruleName} value={ruleName}>
              {ruleName}
            </option>
          ))}
        </select>
        <select
          multiple
          value={selectedRuleNames}
          onChange={(e) => {
            const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
            setSelectedRuleNames(selectedOptions);
          }}
        >
          {getUniqueRuleNames().map((ruleName) => (
            <option key={ruleName} value={ruleName}>
              {ruleName}
            </option>
          ))}
        </select>
      </div>
      <table className="deploytable">
        <thead>
          <tr>
            <th>Date Time</th>
            <th>Deployed Rules</th>
            <th>Status</th>
            <th>Download</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {deploymentData
            .filter(applyFilter)
            .map((item, index) => (
              <tr key={index}>
                <td>{item.datetime}</td>
                <td>{getRuleNames(item.rulesList).join(', ')}</td>
                <td>{item.status}</td>
                <td>
                  <button onClick={() => downloadReport(item.requestID)}>Report</button>
                </td>
                <td>
                  <button id="runButton" onClick={handleClick}>
                    Run again
                  </button>
                  {showPopup && (
                    <div className="popup-deploy">
                      <h3>Select an option:</h3>
                      <div className="popup-content-deploy">
                        <label>
                          <input
                            type="checkbox"
                            checked={selectedOption === 'runLastExecution'}
                            onChange={() => handleOptionSelect('runLastExecution')}
                          />
                          Run Last Execution again
                        </label>
                        <br />
                        <label>
                          <input
                            type="checkbox"
                            checked={selectedOption === 'makeNewExecution'}
                            onChange={() => handleOptionSelect('makeNewExecution')}
                          />
                          Make new execution
                        </label>
                      </div>
                      <br />
                      &emsp;&emsp;&ensp;
                      <button>Run</button>&emsp;&emsp;
                      <button onClick={handleClosePopup}>Close</button>
                    </div>
                  )}
                </td>
              </tr>
            ))
            .reverse()}
        </tbody>
      </table>
    </div>
  );
};

export default DeploymentTable;
