import React, { useState } from 'react';
import { Stage, Layer, Image, Rect } from 'react-konva';
import useImage from 'use-image';
import { FaTrash } from 'react-icons/fa';


const AnnotateImage = ({ imageUrl }) => {
  const [image] = useImage(imageUrl);
  const [rectangles, setRectangles] = useState([]);
  const isDrawing = React.useRef(false);
  const [rectangleTexts, setRectangleTexts] = useState([]);


  const handleMouseDown = (e) => {
    isDrawing.current = true;
    const x = e.evt.x;
    const y = e.evt.y;
    setRectangles([...rectangles, { x, y, width: 0, height: 0 }]);
    // console.log(`Box started: x=${x}, y=${y}`);

  };


  const handleMouseMove = (e) => {
    if (!isDrawing.current) {
      return;
    }
    const currentRect = rectangles[rectangles.length - 1];
    currentRect.width = e.evt.x - currentRect.x;
    currentRect.height = e.evt.y - currentRect.y;

    const newRectangles = rectangles.slice(0, rectangles.length - 1);
    newRectangles.push(currentRect);
    setRectangles(newRectangles);
  };

  const handleMouseUp = () => {
    isDrawing.current = false;
  };

  return (
    <Stage
      width={window.innerWidth}
      height={window.innerHeight}
      onMouseDown={handleMouseDown}
      onMousemove={handleMouseMove}
      onMouseup={handleMouseUp}
    >
      <Layer>
        <Image image={image} />
        {rectangles.map((rect, i) => (
          <Rect
            key={i}
            x={rect.x}
            y={rect.y}
            width={rect.width}
            height={rect.height}
            stroke="red"
            strokeWidth={2}
            draggable
          />
        ))}
      </Layer>
    </Stage>
  );
};

export default AnnotateImage;
