import React, { useState } from 'react';
import './Login.css'
import { BASE_URL } from './config.js';


const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const url = "http://10.1.37.114:5000";
  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };


  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(url+ '/user/validate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // 'Authorization': `Bearer ${localStorage.getItem('accessToken')}` // Include the token in the header
        },
        body: JSON.stringify({
          email: email,
          password: password
        })
      });
      const data = await response.json();
      if (response.ok) {
        // const accessToken = data.access_token;
        // localStorage.setItem('accessToken', accessToken);
        localStorage.setItem('userEmail', email);
        localStorage.setItem('token', data.token);
        const token = localStorage.getItem('token');
        console.log(token);
        alert('Validation successful');
      } else if (data.statusCode === 301) {
        // email not found, do something
        alert(data.message);
      } else if (data.statusCode === 302) {
        // incorrect password, do something
        alert(data.message);
      }
    } catch (error) {
      console.error(error);
    }
  };



  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={handleEmailChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
