import React, { useState, useEffect } from 'react';
import { ReactPictureAnnotation } from 'react-picture-annotation';

const AnnotateImage = ({ image, annotations: initialAnnotations, onChangeAnnotations }) => {
  const [pageSize, setPageSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [annotations, setAnnotations] = useState([]);

  useEffect(() => {
    setAnnotations(initialAnnotations);
  }, [initialAnnotations]);

  console.log('Annotations:', annotations);

  const onResize = () => {
    setPageSize({ width: window.innerWidth, height: window.innerHeight });
  };

  useEffect(() => {
    window.addEventListener('resize', onResize);
    return () => window.removeEventListener('resize', onResize);
  }, []);

  const onSelect = (selectedId) => console.log(selectedId);

  const onChange = (data) => {
    setAnnotations((prevAnnotations) => [...prevAnnotations[0], ...data]);
    onChangeAnnotations([...annotations[0], ...data]);
  };

  const handleDeleteBox = (id) => {
    const updatedAnnotations = annotations.map((annotationGroup) =>
      annotationGroup.filter((annotation) => annotation.id !== id)
    );
    setAnnotations(updatedAnnotations);
    onChangeAnnotations(updatedAnnotations);
  };

  return (
    <div className="AnnotateImage">
      <ReactPictureAnnotation
        image={image}
        onSelect={onSelect}
        onChange={onChange}
        width={pageSize.width}
        height={pageSize.height}
        annotations={annotations[0]}
        renderAnnotation={({ annotation }) => (
          <div style={{ position: 'absolute', left: annotation.mark.x, top: annotation.mark.y }}>
            <input
              type="text"
              value={annotation.comment}
              onChange={(e) => {
                const updatedAnnotations = annotations.map((annotationGroup) =>
                  annotationGroup.map((a) => {
                    if (a.id === annotation.id) {
                      return { ...a, comment: e.target.value };
                    }
                    return a;
                  })
                );
                setAnnotations(updatedAnnotations);
              }}
            />
            <button onClick={() => handleDeleteBox(annotation.id)}>Delete</button>
          </div>
        )}
      />
    </div>
  );
};

export default AnnotateImage;
