import React, { useState, useEffect } from 'react';
import { ReactPictureAnnotation } from 'react-picture-annotation';

const AnnotateImage = ({ image, annotations: initialAnnotations, onChangeAnnotations }) => {

const [pageSize, setPageSize] = useState({
  width: window.innerWidth,
  height: window.innerHeight
});

const [annotations, setAnnotations] = useState([]);

useEffect(() => {
  setAnnotations(initialAnnotations);
}, [initialAnnotations]);
console.log('Annotations:', annotations);

const onResize = () => {
  setPageSize({ width: window.innerWidth, height: window.innerHeight });
};
useEffect(() => {
  window.addEventListener('resize', onResize);
  return () => window.removeEventListener('resize', onResize);
}, []);

const onSelect = (selectedId) => console.log(selectedId);

const onChange = (data) => {
  setAnnotations((prevAnnotations) => {
    const updatedAnnotations = [...prevAnnotations];

    data.forEach((newAnnotation) => {
      const existingAnnotationIndex = updatedAnnotations.findIndex((a) => a.id === newAnnotation.id);

      if (existingAnnotationIndex !== -1) {
        updatedAnnotations[existingAnnotationIndex] = newAnnotation;
      } else {
        // Check if there is a duplicate ID in the new annotations
        const duplicateAnnotationIndex = data.findIndex((a) => a.id === newAnnotation.id);

        if (duplicateAnnotationIndex === data.length - 1) {
          updatedAnnotations.push(newAnnotation);
        }
      }
    });

    return updatedAnnotations;
  });

  onChangeAnnotations((prevAnnotations) => [...prevAnnotations, ...data]);
};





const handleDeleteBox = (id) => {
  const updatedAnnotations = annotations.filter((annotation) => annotation.id !== id);
  setAnnotations(updatedAnnotations);
  onChangeAnnotations(updatedAnnotations);
};
return (
  <div className="AnnotateImage">
    <ReactPictureAnnotation
      image={image}
      onSelect={onSelect}
      onChange={onChange}
      width={pageSize.width}
      height={pageSize.height}
      annotations={annotations}
      renderAnnotation={({ annotation }) => (
        <div style={{ position: 'absolute', left: annotation.position.x, top: annotation.position.y }}>
          <input
            type="text"
            value={annotation.comment}
            onChange={(e) => {
              const updatedAnnotations = [...annotations];
              const index = updatedAnnotations.findIndex((a) => a.id === annotation.id);
              updatedAnnotations[index].comment = e.target.value;
              setAnnotations(updatedAnnotations);
            }}
          />
          <button onClick={() => handleDeleteBox(annotation.id)}>Delete</button>
        </div>
      )}
    />
  </div>
  );
};

export default AnnotateImage;
