import React, { useState } from 'react';

const UploadImages = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [labels, setLabels] = useState([]);

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles(files);
  };

  const handleLabelChange = (event, index) => {
    const updatedLabels = [...labels];
    updatedLabels[index] = event.target.value;
    setLabels(updatedLabels);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Perform validation or additional processing if needed

    // Clear the selected files and labels
    setSelectedFiles([]);
    setLabels([]);
  };

  return (
    <div className="UploadImages">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="files">Choose Files:</label>
          <input type="file" id="files" name="files" accept="image/*" multiple onChange={handleFileChange} />
        </div>
        {selectedFiles.length > 0 && (
          <div>
            <label htmlFor="labels">Labels:</label>
            {selectedFiles.map((file, index) => (
              <div key={file.name}>
                <img src={URL.createObjectURL(file)} alt={file.name} style={{ maxWidth: '200px', maxHeight: '200px' }} />
                <input type="text" id={`label-${index}`} name={`label-${index}`} value={labels[index] || ''} onChange={(e) => handleLabelChange(e, index)} />
              </div>
            ))}
          </div>
        )}
        <button type="submit">Upload</button>
      </form>
    </div>
  );
};

export default UploadImages;
