import React, { useState, useEffect } from 'react';
import './imageannotation.css';
import { useParams } from 'react-router-dom';
import AnnotateImage from './annotate';

const ImageAnnotation = () => {
  const { requestID } = useParams();
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [labels, setLabels] = useState([]);
  const [selectedLabels, setSelectedLabels] = useState([]);
  const [showLabels, setShowLabels] = useState(false); // Add new state for showing labels

  useEffect(() => {
    // Fetch images from API
    fetchImages();
  }, [requestID]);

  useEffect(() => {
    // Fetch labels from API
    fetchLabels();
  }, []); // Fetch labels only once

  const fetchImages = async () => {
    try {
      const response = await fetch('http://10.2.136.111:5000/getImageThumbnailsByRequestID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          requestID: requestID
        })
      });
      const data = await response.json();
      setImages(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchLabels = async () => {
    try {
      const response = await fetch('http://your-api-endpoint-for-fetching-labels');
      const data = await response.json();
      setLabels(data.labels);
    } catch (error) {
      console.error(error);
    }
  };

  const handleImageClick = async (image) => {
    try {
      const response = await fetch('http://10.2.136.111:5000/getImageByImageID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          imageID: image.imageID
        })
      });
      const data = await response.json();
      setSelectedImage(data);
      setSelectedLabels([]);
    } catch (error) {
      console.error(error);
    }
  };

  const handleLabelToggle = (label) => {
    const updatedSelectedLabels = selectedLabels.includes(label)
      ? selectedLabels.filter((selectedLabel) => selectedLabel !== label)
      : [...selectedLabels, label];

    setSelectedLabels(updatedSelectedLabels);
  };

  const handleDoneClick = () => {
    // Perform logic to save selected labels for the selected image
    console.log('Selected Labels:', selectedLabels);
    setShowLabels(true); // Show labels after clicking "Done"
  };

  const handleResetClick = () => {
    setSelectedLabels([]);
    setShowLabels(false); // Hide labels on reset
  };

  return (
    <div className="image-annotation-container">
      <div className="image-list">
        {images.map((image) => (
          <div
            key={image.imageID}
            className={`image-thumbnail ${selectedImage && selectedImage.imageID === image.imageID ? 'selected' : ''}`}
            onClick={() => handleImageClick(image)}
          >
            <img src={`data:image/jpeg;base64,${image.imageThumbnail}`} alt={`Image ${image.imageID}`} />
          </div>
        ))}
      </div>
      <div className="annotation-section">
        {selectedImage && (
          <div className="selected-image">
            <AnnotateImage image={`data:image/jpeg;base64,${selectedImage.base64Image}`} className="annotation-image" />
          </div>
        )}

        {/* {selectedImage && (
        <div className="selected-image">
          <img
            src={`data:image/jpeg;base64,${selectedImage.base64Image}`}
            alt={`Image ${selectedImage.imageID}`}
            className="selected-image__img"
          />
        </div>
      )} */}
        <div className="label-section">
          <h3>Labels</h3>
          {showLabels && labels.map((label) => (
            <div
              key={label.id}
              className={`label ${selectedLabels.includes(label) ? 'selected' : ''}`}
              onClick={() => handleLabelToggle(label)}
            >
              {label.name}
            </div>
          ))}
        </div>
        <div className="button-section">
          <button className="done-button" onClick={handleDoneClick}>Done</button>
          <button className="reset-button" onClick={handleResetClick}>Reset</button>
        </div>
      </div>
    </div>
  );
};

export default ImageAnnotation;
