import React, { useState } from 'react';
import { Stage, Layer, Image, Rect, Transformer } from 'react-konva';
import useImage from 'use-image';

const AnnotateImage = ({ imageUrl }) => {
  const [image] = useImage(imageUrl);
  const [rectangles, setRectangles] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const isDrawing = React.useRef(false);

  const handleMouseDown = (e) => {
    isDrawing.current = true;
    const x = e.evt.x;
    const y = e.evt.y;
    setRectangles([...rectangles, { id: rectangles.length, x, y, width: 0, height: 0 }]);
  };

  const handleMouseMove = (e) => {
    if (!isDrawing.current) {
      return;
    }
    const currentRect = rectangles[rectangles.length - 1];
    currentRect.width = e.evt.x - currentRect.x;
    currentRect.height = e.evt.y - currentRect.y;

    const newRectangles = rectangles.slice();
    newRectangles.splice(rectangles.length - 1, 1, currentRect);
    setRectangles(newRectangles);
  };

  const handleMouseUp = () => {
    isDrawing.current = false;
  };

  const handleRectClick = (e, id) => {
    setSelectedId(id);
  };

  return (
    <Stage
      width={window.innerWidth}
      height={window.innerHeight}
      onMouseDown={handleMouseDown}
      onMousemove={handleMouseMove}
      onMouseup={handleMouseUp}
    >
      <Layer>
        <Image image={image} />
        {rectangles.map((rect) => (
          <React.Fragment key={rect.id}>
            <Rect
              x={rect.x}
              y={rect.y}
              width={rect.width}
              height={rect.height}
              stroke={selectedId === rect.id ? 'red' : 'black'}
              strokeWidth={2}
              onClick={(e) => handleRectClick(e, rect.id)}
              draggable
            />
            {selectedId === rect.id && (
              <Transformer
                boundBoxFunc={(oldBox, newBox) => {
                  // Limit the minimum size of the rectangle
                  newBox.width = Math.max(10, newBox.width);
                  newBox.height = Math.max(10, newBox.height);
                  return newBox;
                }}
                keepRatio={false}
                resizeEnabled={true}
              />
            )}
          </React.Fragment>
        ))}
      </Layer>
    </Stage>
  );
};

export default AnnotateImage;
