import React, { useState, useEffect } from 'react';
import './imageannotation.css';
import { useParams } from 'react-router-dom';
import AnnotateImage from './annotate';

const ImageAnnotation = () => {
  const { requestID } = useParams();
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [labels, setLabels] = useState([]);
  const [annotations, setAnnotations] = useState([]);
  const [filterOption, setFilterOption] = useState('all');

  useEffect(() => {
    // Fetch images from API
    fetchImages();
  }, [requestID]);

  const fetchImages = async () => {
    try {
      const response = await fetch('http://10.2.8.173:5000/getImageThumbnailsByRequestID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          requestID: requestID
        })
      });
      const data = await response.json();
      setImages(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleImageClick = async (image) => {
    try {
      const response = await fetch('http://10.2.8.173:5000/getImageByImageID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          imageID: image.imageID
        })
      });
      const data = await response.json();
      setSelectedImage(data);
      setAnnotations([]);
    } catch (error) {
      console.error(error);
    }
  };

  const handleLabelToggle = (label) => {
    const updatedAnnotations = [...annotations];
    const selectedImageAnnotations = updatedAnnotations.find(a => a.imageID === selectedImage.imageID);

    if (selectedImageAnnotations) {
      const labelIndex = selectedImageAnnotations.labels.findIndex(l => l.id === label.id);
      if (labelIndex !== -1) {
        selectedImageAnnotations.labels.splice(labelIndex, 1);
      } else {
        selectedImageAnnotations.labels.push(label);
      }
    } else {
      updatedAnnotations.push({
        imageID: selectedImage.imageID,
        labels: [label]
      });
    }

    setAnnotations(updatedAnnotations);
  };

  // const handleDoneClick = () => {
  //   const hasUndefinedLabels = annotations.some(a => a.label === undefined);
  //   if (hasUndefinedLabels) {
  //     alert('Please add all labels before submitting.');
  //   } else {
  //     // Perform logic to save selected labels for the selected image
  //     console.log('Annotations:', annotations);

  //     if (selectedImage) {
  //       const updatedImages = images.map(image =>
  //         image.imageID === selectedImage.imageID ? { ...image, status: 'Annotated' } : image
  //       );
  //       setImages(updatedImages);
  //     }
  //   }
  // };

  const handleDoneClick = async () => {
    const hasUndefinedLabels = annotations.some(a => a.labels.some(l => l === undefined));
    if (hasUndefinedLabels) {
      alert('Please select all labels before submitting.');
    } else {
      try {
        // Prepare the annotations payload
        const annotationPayload = {
          imageID: selectedImage.imageID,
          annotations: annotations.map(a => ({
            id: a.imageID,
            label: a.labels.map(l => l.name),
            coordinates: a.labels.map(l => l.coordinates)
          }))
        };

        // Send the annotations to the API endpoint
        const response = await fetch('http://10.2.138.62:5000/setAnnotations', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(annotationPayload)
        });

        if (response.ok) {
          // Update the status to 'Annotated'
          const updatedImages = images.map(image =>
            image.imageID === selectedImage.imageID ? { ...image, status: 'Annotated' } : image
          );
          setImages(updatedImages);

          console.log('Annotations sent:', annotationPayload);
          alert('Annotations submitted successfully.');
        } else {
          throw new Error('Failed to submit annotations.');
        }
      } catch (error) {
        console.error(error);
        alert('Failed to submit annotations. Please try again.');
      }
    }
  };



  const handleResetClick = () => {
    setAnnotations([]);
  };

  const handleAnnotationChange = (updatedAnnotations) => {
    setAnnotations(updatedAnnotations);
  };

  const handleFilterChange = (option) => {
    setFilterOption(option);
  };

  const filteredImages = filterOption === 'all' ? images : images.filter((image) => image.status === filterOption);

  return (
    <div className="image-annotation-container">
      <div className="image-filter">
        <select value={filterOption} onChange={(e) => handleFilterChange(e.target.value)}>
          <option value="all">All</option>
          <option value="Unannotated">Unannotated</option>
          <option value="Annotated">Annotated</option>
        </select>
      </div>
      <div className="image-list">
        <br></br>
        {filteredImages.map((image) => (
          <div
            key={image.imageID}
            className={`image-thumbnail ${selectedImage && selectedImage.imageID === image.imageID ? 'selected' : ''}`}
            onClick={() => handleImageClick(image)}
          >
            <img src={`data:image/jpeg;base64,${image.imageThumbnail}`} alt={`Image ${image.imageID}`} />
            {image.status === 'Unannotated' ? (
              <div className="button-circle red"></div>
            ) : (
              <div className="button-circle green"></div>
            )}
          </div>
        ))}
      </div>
      <div className="annotation-section">
        {selectedImage && (
          <div className="selected-image">
            <AnnotateImage
              image={`data:image/jpeg;base64,${selectedImage.base64Image}`}
              onChangeAnnotations={handleAnnotationChange}
              className="annotation-image"
            />
          </div>
        )}

        <div className="label-section">
          <h3>Labels</h3>
          {labels && labels.map((label) => (
            <div
              key={label.id}
              className={`label ${annotations.some(a => a.labels.some(l => l.id === label.id)) ? 'selected' : ''}`}
              onClick={() => handleLabelToggle(label)}
            >
              {label.name}
            </div>
          ))}
        </div>
        <div className="button-section">
          <button className="done-button" onClick={handleDoneClick}>
            Done
          </button>
          <button className="reset-button" onClick={handleResetClick}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
};

export default ImageAnnotation;
