import React, { useState, useEffect } from 'react';
import './imageannotation.css';
import { useParams } from 'react-router-dom';
import AnnotateImage from './annotate';

const ImageAnnotation = () => {
  const { requestID } = useParams();
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [labels, setLabels] = useState([]);
  const [selectedLabels, setSelectedLabels] = useState([]);
  const [annotations, setAnnotations] = useState([]);
  const [filterOption, setFilterOption] = useState('all');

  useEffect(() => {
    // Fetch images and labels from API
    fetchImages();
    fetchLabels();
  }, [requestID]);

  const fetchImages = async () => {
    try {
      const response = await fetch('http://10.2.136.111:5000/getImageThumbnailsByRequestID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          requestID: requestID
        })
      });
      const data = await response.json();
      setImages(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchLabels = async () => {
    try {
      const response = await fetch('http://10.2.136.111:5000/getLabels', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          requestID: requestID
        })
      });
      const data = await response.json();
      setLabels(data.labels);
    } catch (error) {
      console.error(error);
    }
  };

  const handleImageClick = async (image) => {
    try {
      const response = await fetch('http://10.2.136.111:5000/getImageByImageID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          imageID: image.imageID
        })
      });
      const data = await response.json();
      setSelectedImage(data);
      setSelectedLabels([]);
      setAnnotations([]);
    } catch (error) {
      console.error(error);
    }
  };

  const handleLabelToggle = (label) => {
    const updatedSelectedLabels = selectedLabels.includes(label)
      ? selectedLabels.filter((selectedLabel) => selectedLabel !== label)
      : [...selectedLabels, label];

    setSelectedLabels(updatedSelectedLabels);
  };

  const handleDoneClick = () => {
    if (selectedLabels.some((label) => label === undefined)) {
      alert('Please select all labels before submitting.');
    } else {
      // Perform logic to save selected labels for the selected image
      console.log('Selected Labels:', selectedLabels);
      console.log('Annotations:', annotations);

      const selectedLabelsFormatted = selectedLabels.map((label) => label.name);
      // Update the state with the selected labels
      setAnnotations([...annotations, { labels: selectedLabelsFormatted }]);
    }
  };

  const handleResetClick = () => {
    setSelectedLabels([]);
    setAnnotations([]);
  };

  const handleAnnotationChange = (updatedAnnotations) => {
    setAnnotations(updatedAnnotations);
  };

  const handleFilterChange = (option) => {
    setFilterOption(option);
  };

  const filteredImages = filterOption === 'all' ? images : images.filter((image) => image.status === filterOption);

  return (
    <div className="image-annotation-container">
      <div className="image-filter">
        <select value={filterOption} onChange={(e) => handleFilterChange(e.target.value)}>
          <option value="all">All</option>
          <option value="Unannotated">Unannotated</option>
          <option value="Annotated">Annotated</option>
        </select>
      </div>
      <div className="image-list">
        <br></br>
        {filteredImages.map((image) => (
          <div
            key={image.imageID}
            className={`image-thumbnail ${selectedImage && selectedImage.imageID === image.imageID ? 'selected' : ''}`}
            onClick={() => handleImageClick(image)}
          >
            <img src={`data:image/jpeg;base64,${image.imageThumbnail}`} alt={`Image ${image.imageID}`} />
            {image.status === 'Unannotated' ? (
              <div className="button-circle red"></div>
            ) : (
              <div className="button-circle green"></div>
            )}
          </div>
        ))}
      </div>
      <div className="annotation-section">
        {selectedImage && (
          <div className="selected-image">
            <AnnotateImage
              image={`data:image/jpeg;base64,${selectedImage.base64Image}`}
              onChangeAnnotations={handleAnnotationChange}
              className="annotation-image"
            />
          </div>
        )}

        <div className="label-section">
          <h3>Labels</h3>
          {labels.map((label) => (
            <div
              key={label.id}
              className={`label ${selectedLabels.includes(label) ? 'selected' : ''}`}
              onClick={() => handleLabelToggle(label)}
            >
              {label.name}
            </div>
          ))}
        </div>
        <div className="button-section">
          <button className="done-button" onClick={handleDoneClick}>
            Done
          </button>
          <button className="reset-button" onClick={handleResetClick}>
            Reset
          </button>
        </div>
      </div>
      <div className="labels-all-section">
        <h3>Labels All</h3>
        {annotations.map((annotation, index) => (
          <div key={index} className="annotation-labels">
            {annotation.labels.map((label, index) => (
              <span key={index} className="label-name">{label}</span>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageAnnotation;
