import React, { useEffect } from 'react';
import { Link, NavLink, Outlet } from 'react-router-dom';
import logo from '../IIITH_logo.png';
import 'bootstrap/dist/css/bootstrap.css';
// import 'bootstrap/dist/js/bootstrap.bundle';

const Layout = () => {
  useEffect(() => {
    // Initialize Bootstrap dropdown
    const dropdowns = document.querySelectorAll('.dropdown-toggle');
    dropdowns.forEach((dropdown) => {
      dropdown.addEventListener('click', () => {
        dropdown.nextElementSibling.classList.toggle('show');
      });
    });

    // Close dropdown when clicked outside
    window.addEventListener('click', (event) => {
      if (!event.target.matches('.dropdown-toggle')) {
        const dropdowns = document.querySelectorAll('.dropdown-menu');
        dropdowns.forEach((dropdown) => {
          if (dropdown.classList.contains('show')) {
            dropdown.classList.remove('show');
          }
        });
      }
    });
  }, []);

  return (
    <div className="container">
      {/* Logo and Heading */}
      <div className="d-flex align-items-center">
        <img src={logo} alt="Logo" width="180" />
        <h1 className="ml-3">Visual Inspection Tool</h1>
      </div>

      {/* Navigation Bar */}
      <nav className="mt-3">
        <ul className="nav">
          <li className="nav-item dropdown">
            <button className="nav-link dropdown-toggle btn" type="button" data-toggle="dropdown">
              Dataset
            </button>
            <ul className="dropdown-menu">
              <li>
                <Link to="/dataset/view" className="dropdown-item">View Dataset</Link>
              </li>
              <li>
                <Link to="/dataset/upload" className="dropdown-item">Upload Dataset</Link>
              </li>
            </ul>
          </li>
          <li className="nav-item dropdown">
            <button className="nav-link dropdown-toggle btn" type="button" data-toggle="dropdown">
              Model
            </button>
            <ul className="dropdown-menu">
              <li>
                <Link to="/model/train" className="dropdown-item">Train Model</Link>
              </li>
            </ul>
          </li>
          <li className="nav-item">
            <NavLink to="/login" className="nav-link">Logout</NavLink>
          </li>
        </ul>
      </nav>

      {/* Render the current route */}
      <Outlet />
    </div>
  );
};

export default Layout;
