import React, { useState, useEffect } from 'react';
import { ReactPictureAnnotation } from 'react-picture-annotation';

const AnnotateImage = () => {
  const [pageSize, setPageSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });
  const [annotations, setAnnotations] = useState([]);
  const [annotationsData, setAnnotationsData] = useState('');

  const onResize = () => {
    setPageSize({ width: window.innerWidth, height: window.innerHeight });
  };

  useEffect(() => {
    window.addEventListener('resize', onResize);
    return () => window.removeEventListener('resize', onResize);
  }, []);

  const onSelect = (selectedId) => console.log(selectedId);

  const onChange = (data) => {
    setAnnotations(data);
  };

  const handleDeleteBox = (id) => {
    const updatedAnnotations = annotations.filter((annotation) => annotation.id !== id);
    setAnnotations(updatedAnnotations);
  };

  const getAnnotationYoloFormat = (annotation) => {
    const x_center = (annotation.position.x + annotation.position.width / 2) / pageSize.width;
    const y_center = (annotation.position.y + annotation.position.height / 2) / pageSize.height;
    const width = annotation.position.width / pageSize.width;
    const height = annotation.position.height / pageSize.height;
    return `${annotation.id} [${x_center.toFixed(4)}, ${y_center.toFixed(4)}, ${width.toFixed(4)}, ${height.toFixed(4)}]`;
  };

  const generateAnnotationsData = () => {
    const data = annotations.map((annotation) => getAnnotationYoloFormat(annotation)).join('\n');
    setAnnotationsData(data);
  };

  return (
    <div className="AnnotateImage">
      <ReactPictureAnnotation
        image="https://encrypted-tbn1.gstatic.com/licensed-image?q=tbn:ANd9GcT7p48zgkqUyYDdx7AGnlWVOEh6vpc8QHHPh0mAtn_73_jD_etGCyAexD1ArARBsGl4HqZYyGjcpRJRo3A" // Update the image URL
        onSelect={onSelect}
        onChange={onChange}
        width={pageSize.width}
        height={pageSize.height}
        annotations={annotations}
        renderContent={({ annotation }) => (
          <div>
            <input
              type="text"
              value={annotation.content}
              onChange={(e) => {
                const updatedAnnotations = [...annotations];
                const index = updatedAnnotations.findIndex((a) => a.id === annotation.id);
                updatedAnnotations[index].content = e.target.value;
                setAnnotations(updatedAnnotations);
              }}
            />
            <div>{getAnnotationYoloFormat(annotation)}</div>
            <button onClick={() => handleDeleteBox(annotation.id)}>Delete</button>
          </div>
        )}
      />
      <button onClick={generateAnnotationsData}>Download Annotations</button>
      <textarea
        readOnly
        rows={10}
        cols={50}
        style={{ display: 'none' }}
        value={annotationsData}
        ref={(textarea) => {
          if (textarea) {
            textarea.focus();
            textarea.select();
            document.execCommand('copy');
          }
        }}
      />
    </div>
  );
};

export default AnnotateImage
