import React, { useState } from 'react';
import Dropzone from 'react-dropzone';

const UploadImages = () => {
  const [images, setImages] = useState([]);
  const [labels, setLabels] = useState([]);

  const handleDrop = (acceptedFiles) => {
    // Filter out non-image files
    const imageFiles = acceptedFiles.filter(
      (file) =>
        file.type === 'image/jpeg' ||
        file.type === 'image/png' ||
        file.type === 'image/gif' ||
        file.type === 'image/bmp'
    );

    // Update state with new image files
    setImages((prevState) => [...prevState, ...imageFiles]);
    setLabels((prevState) => [...prevState, ...Array(imageFiles.length)]);
  };

  const handleLabelChange = (index, label) => {
    setLabels((prevState) => {
      const updatedLabels = [...prevState];
      updatedLabels[index] = label;
      return updatedLabels;
    });
  };

  return (
    <div>
      <Dropzone onDrop={handleDrop} accept="image/jpeg, image/png, image/gif, image/bmp" multiple>
        {({ getRootProps, getInputProps }) => (
          <div {...getRootProps()}>
            <input {...getInputProps()} />
            <p>Drag and drop some files here, or click to select files</p>
          </div>
        )}
      </Dropzone>

      {images.length > 0 && (
        <div>
          <h2>Image Labels</h2>
          {images.map((image, index) => (
            <div key={index}>
              <img src={URL.createObjectURL(image)} alt="uploaded" />
              <input
                type="text"
                value={labels[index]}
                onChange={(e) => handleLabelChange(index, e.target.value)}
              />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UploadImages;
