import React, { useState, useEffect } from 'react';
// import ReactPictureAnnotation from 'react-picture-annotation';
import { ReactPictureAnnotation } from 'react-picture-annotation';


const App = () => {
  const [pageSize, setPageSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });
  const [annotations, setAnnotations] = useState([]);

  const onResize = () => {
    setPageSize({ width: window.innerWidth, height: window.innerHeight });
  };

  useEffect(() => {
    window.addEventListener('resize', onResize);
    return () => window.removeEventListener('resize', onResize);
  }, []);

  const onSelect = (selectedId) => console.log(selectedId);

  const onChange = (data) => {
    setAnnotations(data);
  };

  const handleDeleteBox = (id) => {
    const updatedAnnotations = annotations.filter((annotation) => annotation.id !== id);
    setAnnotations(updatedAnnotations);
  };

  return (
    <div className="App">
      <ReactPictureAnnotation
        image="https://source.unsplash.com/random/https://encrypted-tbn1.gstatic.com/licensed-image?q=tbn:ANd9GcT7p48zgkqUyYDdx7AGnlWVOEh6vpc8QHHPh0mAtn_73_jD_etGCyAexD1ArARBsGl4HqZYyGjcpRJRo3A"
        onSelect={onSelect}
        onChange={onChange}
        width={pageSize.width}
        height={pageSize.height}
        annotations={annotations}
        renderContent={(props) => (
          <React.Fragment>
            {props.annotation && (
              <div style={{ position: 'absolute', left: props.annotation.position.x, top: props.annotation.position.y }}>
                <input
                  type="text"
                  value={props.annotation.content}
                  onChange={(e) => {
                    const updatedAnnotations = [...annotations];
                    const index = updatedAnnotations.findIndex((a) => a.id === props.annotation.id);
                    updatedAnnotations[index].content = e.target.value;
                    setAnnotations(updatedAnnotations);
                  }}
                />
                <button onClick={() => handleDeleteBox(props.annotation.id)}>Delete</button>
              </div>
            )}
          </React.Fragment>
        )}
      />
    </div>
  );
};

export default App;
