import React, { useState } from 'react';

const UploadImages = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [labels, setLabels] = useState([]);

  const handleFileChange = (event) => {
    const files = event.target.files;
    const selectedFilesArray = Array.from(files);

    setSelectedFiles(selectedFilesArray);

    // Reset labels array when selecting new files
    setLabels(Array(selectedFilesArray.length).fill(''));
  };

  const handleLabelChange = (index, event) => {
    const updatedLabels = [...labels];
    updatedLabels[index] = event.target.value;
    setLabels(updatedLabels);
  };

  const handleUpload = () => {
    // Perform upload logic here
    // Access selectedFiles and labels arrays to handle the uploaded files and their respective labels
    console.log(selectedFiles);
    console.log(labels);
  };

  return (
    <div className="UploadImages">
      <input type="file" multiple accept="image/*" onChange={handleFileChange} />

      {selectedFiles.map((file, index) => (
        <div key={index} className="ImageContainer">
          <img src={URL.createObjectURL(file)} alt={`Image ${index + 1}`} />

          <label>
            Label:
            <input
              type="text"
              value={labels[index]}
              onChange={(event) => handleLabelChange(index, event)}
            />
          </label>
        </div>
      ))}

      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

export default UploadImages;
