import React, { useState, useEffect } from 'react';
import { ReactPictureAnnotation } from 'react-picture-annotation';


const AnnotateImage = () => {
  const [pageSize, setPageSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });
  const [annotations, setAnnotations] = useState([]);

  const onResize = () => {
    setPageSize({ width: window.innerWidth, height: window.innerHeight });
  };

  useEffect(() => {
    window.addEventListener('resize', onResize);
    return () => window.removeEventListener('resize', onResize);
  }, []);

  const onSelect = (selectedId) => console.log(selectedId);

  // const onChange = (data) => {
  //   setAnnotations(data);
  //   console.log(data);
  // };

  // const onChange = (data) => {
  //   setAnnotations(data);
  //   data.forEach((annotation) => {
  //     const { x, y, width, height } = annotation.mark;
  //     console.log(`x: ${x}, y: ${y}, width: ${width}, height: ${height}`);
  //   });
  // };

  // const onChange = (data) => {
  //   setAnnotations(data);
  //   data.forEach((annotation) => {
  //     const { id, mark } = annotation;
  //     const { x, y, width, height } = mark;

  //     // Calculate normalized values
  //     const imageWidth = pageSize.width;
  //     const imageHeight = pageSize.height;
  //     const xCenter = (x + width / 2) / imageWidth;
  //     const yCenter = (y + height / 2) / imageHeight;
  //     const normalizedWidth = width / imageWidth;
  //     const normalizedHeight = height / imageHeight;

  //     console.log(`ID: ${id}`);
  //     console.log(`YOLO [x_center, y_center, width, height] in normalized form: [${xCenter}, ${yCenter}, ${normalizedWidth}, ${normalizedHeight}]`);
  //   });
  // };

  const fs = require('fs'); // Import the 'fs' module for file operations

const onChange = (data) => {
  setAnnotations(data);
  const yoloData = data.map((annotation) => {
    const { id, mark } = annotation;
    const { x, y, width, height } = mark;

    // Calculate normalized values
    const imageWidth = pageSize.width;
    const imageHeight = pageSize.height;
    const xCenter = (x + width / 2) / imageWidth;
    const yCenter = (y + height / 2) / imageHeight;
    const normalizedWidth = width / imageWidth;
    const normalizedHeight = height / imageHeight;

    return {
      id,
      yolo: [xCenter, yCenter, normalizedWidth, normalizedHeight],
    };
  });

  // Convert yoloData to JSON string
  const jsonString = JSON.stringify(yoloData, null, 2);

  // Write the JSON string to a file
  fs.writeFile('annotations.json', jsonString, (err) => {
    if (err) {
      console.error('Error writing annotations file:', err);
    } else {
      console.log('Annotations file saved successfully.');
    }
  });
};




  const handleDeleteBox = (id) => {
    const updatedAnnotations = annotations.filter((annotation) => annotation.id !== id);
    setAnnotations(updatedAnnotations);
  };

  return (
    <div className="AnnotateImage">
      <ReactPictureAnnotation
        image="https://encrypted-tbn1.gstatic.com/licensed-image?q=tbn:ANd9GcT7p48zgkqUyYDdx7AGnlWVOEh6vpc8QHHPh0mAtn_73_jD_etGCyAexD1ArARBsGl4HqZYyGjcpRJRo3A" // Update the image URL
        onSelect={onSelect}
        onChange={onChange}
        width={pageSize.width}
        height={pageSize.height}
        annotations={annotations}
        renderAnnotation={({ annotation }) => (
          <div style={{ position: 'absolute', left: annotation.position.x, top: annotation.position.y }}>
            <input
              type="text"
              value={annotation.content}
              onChange={(e) => {
                const updatedAnnotations = [...annotations];
                const index = updatedAnnotations.findIndex((a) => a.id === annotation.id);
                updatedAnnotations[index].content = e.target.value;
                setAnnotations(updatedAnnotations);
              }}
            />
            <button onClick={() => handleDeleteBox(annotation.id)}>Delete</button>
          </div>
        )}
      />
    </div>
  );
};

export default AnnotateImage;


