import React, { useState } from 'react';
import image from './as.png';


const ImageAnnotator = () => {
  const [boxes, setBoxes] = useState([]);
  const [startPoint, setStartPoint] = useState(null);

  const handleMouseDown = (e) => {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left; 
    const y = e.clientY - rect.top;
    setStartPoint({ x, y });
  }

  const handleMouseUp = (e) => {
    if (!startPoint) return;

    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left; 
    const y = e.clientY - rect.top;

    const newBox = {
      x: Math.min(startPoint.x, x),
      y: Math.min(startPoint.y, y),
      width: Math.abs(startPoint.x - x),
      height: Math.abs(startPoint.y - y),
    };

    setBoxes(prevBoxes => [...prevBoxes, newBox]);
    setStartPoint(null);
  }

  const [activeBox, setActiveBox] = useState(null);

const handleBoxClick = (boxIndex) => {
  setActiveBox(boxIndex);
};

const handleBoxDrag = (e, boxIndex) => {
  const rect = e.currentTarget.getBoundingClientRect();
  const x = e.clientX - rect.left;
  const y = e.clientY - rect.top;

  const newBoxes = [...boxes];
  newBoxes[boxIndex] = {
    ...newBoxes[boxIndex],
    x,
    y,
  };

  setBoxes(newBoxes);
};

const handleBoxResize = (e, boxIndex) => {
  const rect = e.currentTarget.getBoundingClientRect();
  const x = e.clientX - rect.left;
  const y = e.clientY - rect.top;

  const newBoxes = [...boxes];
  newBoxes[boxIndex] = {
    ...newBoxes[boxIndex],
    width: Math.max(x - newBoxes[boxIndex].x, 10),
    height: Math.max(y - newBoxes[boxIndex].y, 10),
  };

  setBoxes(newBoxes);
};

return (
  <div style={{ position: 'relative' }}>
    <img
      src={image}
      style={{ width: '100%' }}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
    />

    {boxes.map((box, i) => (
      <div
        key={i}
        style={{
          position: 'absolute',
          border: '2px solid red',
          left: box.x,
          top: box.y,
          width: box.width,
          height: box.height,
          cursor: 'move',
        }}
        onClick={() => handleBoxClick(i)}
        onMouseDown={(e) => {
          e.stopPropagation();
          e.currentTarget.style.zIndex = '100';
        }}
        onMouseUp={(e) => {
          e.currentTarget.style.zIndex = 'auto';
        }}
        onMouseMove={(e) => {
          if (activeBox !== null) {
            handleBoxDrag(e, activeBox);
          }
        }}
      >
        <div
          style={{
            position: 'absolute',
            width: '10px',
            height: '10px',
            background: 'white',
            border: '2px solid red',
            right: 0,
            bottom: 0,
            cursor: 'se-resize',
          }}
          onMouseDown={(e) => {
            e.stopPropagation();
            e.currentTarget.style.zIndex = '100';
          }}
          onMouseUp={(e) => {
            e.currentTarget.style.zIndex = 'auto';
          }}
          onMouseMove={(e) => {
            if (activeBox !== null) {
              handleBoxResize(e, activeBox);
            }
          }}
        />
      </div>
    ))}
  </div>
);


  return (
    <div style={{ position: 'relative' }}>
      <img 
        src={image} 
        style={{ width: '100%' }} 
        onMouseDown={handleMouseDown} 
        onMouseUp={handleMouseUp} 
      />

      {boxes.map((box, i) => (
        <div 
          key={i}
          style={{
            position: 'absolute',
            border: '2px solid red',
            left: box.x,
            top: box.y,
            width: box.width,
            height: box.height,
          }}
        />
      ))}
    </div>
  );
};

export default ImageAnnotator;
