import React, { useState, useEffect } from 'react';

const App = () => {
  const [rules, setRules] = useState([]);

  useEffect(() => {
    fetch('http://10.1.37.114:5000/getRules', {
      method: 'POST'
    })
      .then(response => response.json())
      .then(data => setRules(data.data))
      .catch(error => console.log(error));
  }, []);

  const [selectedRule, setSelectedRule] = useState(null);

  const handleViewDescription = (rule) => {
    setSelectedRule(rule);
  };

  const closeModal = () => {
    setSelectedRule(null);
  };

  return (
    <div>
      {/* ... */}
      {selectedRule && (
        <div className="modal">
          <div className="modal-content">
            <h2>Rule Description</h2>
            <table>
              <thead>
                <tr>
                  <th>Category</th>
                  <th>Detection</th>
                  <th>Label</th>
                </tr>
              </thead>
              <tbody>
                {selectedRule['Rule Description'].map((description, index) => (
                  <tr key={index}>
                    <td>{description.category}</td>
                    <td>{description.detection}</td>
                    <td>{description.label}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};


export default TableComponent;
