import React, { useState, useEffect } from 'react';
import './viewrules.css'; 

const TableComponent = () => {
  const [rules, setRules] = useState([]);

  const fetchRules = async () => {
    try {
      const response = await fetch('http://10.1.37.114:5000/getRules', {
        method: 'POST'
      });
      const data = await response.json();
      setRules(data.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchRules();
  }, []);

  const [showDescription, setShowDescription] = useState(false);
  const [descriptionData, setDescriptionData] = useState([]);

  const handleViewDescription = (description) => {
    setDescriptionData(description);
    setShowDescription(true);
  };

  return (
    <div>
      <button onClick={fetchRules}>Deploy</button>
      <table className="rulestable"> {/* Add "rulestable" class to the table */}
        <thead>
          <tr>
            <th>Checklist</th>
            <th>Model Name</th>
            <th>Rule Name</th>
            <th>Rule Creation Date</th>
            <th>Rule Description</th>
          </tr>
        </thead>
        <tbody>
          {rules.map((rule, index) => (
            <tr key={index}>
              <td>
                <input type="checkbox" />
              </td>
              <td>{rule['Model Name']}</td>
              <td>{rule['Rule Name']}</td>
              <td>{rule['Rule Creation Date']}</td>
              <td>
                <button
                  onClick={() =>
                    handleViewDescription(rule['Rule Description'])
                  }
                >
                  View Description
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showDescription && (
        <div className="popup">
          <div className="popup-content">
            <h3>Description</h3>
            <table className="rulestable"> {/* Add "rulestable" class to the table */}
              <thead>
                <tr>
                  <th>Category</th>
                  <th>Detection</th>
                  <th>Label</th>
                </tr>
              </thead>
              <tbody>
                {descriptionData.map((description, index) => (
                  <tr key={index}>
                    <td>{description.category}</td>
                    <td>{description.detection}</td>
                    <td>{description.label}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button onClick={() => setShowDescription(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TableComponent;
