import React, { useState, useEffect } from 'react';
import './imageannotation.css';
import { useParams } from 'react-router-dom';
import AnnotateImage from './annotate';

const ImageAnnotation = () => {
  const { requestID } = useParams();
  const [images, setImages] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [labels, setLabels] = useState([]);
  const [selectedLabels, setSelectedLabels] = useState([]);
  const [labelData, setLabelData] = useState([]);

  useEffect(() => {
    // Fetch images from API
    fetchImages();
  }, [requestID]);

  useEffect(() => {
    // Update labels when selected image changes
    if (selectedImage) {
      setLabels(selectedImage.labels);
    }
  }, [selectedImage]);

  const fetchImages = async () => {
    try {
      const response = await fetch('http://10.2.136.18:5000/getImageThumbnailsByRequestID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          requestID: requestID
        })
      });
      const data = await response.json();
      setImages(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleImageClick = async (image) => {
    try {
      const response = await fetch('http://10.2.136.18:5000/getImageByImageID', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          imageID: image.imageID
        })
      });
      const data = await response.json();
      setSelectedImage(data);
      setSelectedLabels([]);
      setLabelData([]); // Reset label data when a new image is selected
    } catch (error) {
      console.error(error);
    }
  };

  const handleLabelToggle = (label) => {
    const updatedSelectedLabels = selectedLabels.includes(label)
      ? selectedLabels.filter((selectedLabel) => selectedLabel !== label)
      : [...selectedLabels, label];

    setSelectedLabels(updatedSelectedLabels);
  };

  const handleDoneClick = () => {
    // Store selected labels and corresponding YOLO coordinates
    const updatedLabelData = selectedLabels.map((label) => {
      return {
        label: label.name,
        coordinates: calculateYoloCoordinates(label)
      };
    });
    setLabelData(updatedLabelData);

    console.log('Selected Labels:', updatedLabelData);
  };

  const calculateYoloCoordinates = (label) => {
    const { mark } = label;
    const { x, y, width, height } = mark;

    // Calculate normalized values
    const imageWidth = selectedImage.width;
    const imageHeight = selectedImage.height;
    const xCenter = (x + width / 2) / imageWidth;
    const yCenter = (y + height / 2) / imageHeight;
    const normalizedWidth = width / imageWidth;
    const normalizedHeight = height / imageHeight;

    return [xCenter, yCenter, normalizedWidth, normalizedHeight];
  };

  const handleResetClick = () => {
    setSelectedLabels([]);
    setLabelData([]);
  };

  return (
    <div className="image-annotation-container">
      <div className="image-list">
        {images.map((image) => (
          <div
            key={image.imageID}
            className={`image-thumbnail ${selectedImage && selectedImage.imageID === image.imageID ? 'selected' : ''}`}
            onClick={() => handleImageClick(image)}
          >
            <img src={`data:image/jpeg;base64,${image.imageThumbnail}`} alt={`Image ${image.imageID}`} />
          </div>
        ))}
      </div>
      <div className="annotation-section">
        {selectedImage && (
          <div className="selected-image">
            <AnnotateImage image={`data:image/jpeg;base64,${selectedImage.base64Image}`} />
          </div>
        )}
        <div className="label-section">
          <h3>Labels</h3>
          {labels.map((label) => (
            <div
              key={label.id}
              className={`label ${selectedLabels.includes(label) ? 'selected' : ''}`}
              onClick={() => handleLabelToggle(label)}
            >
              {label.name}
            </div>
          ))}
        </div>
        <div className="button-section">
          <button className="done-button" onClick={handleDoneClick}>Done</button>
          <button className="reset-button" onClick={handleResetClick}>Reset</button>
        </div>
      </div>
      <div className="label-section">
        <h3>Selected Labels</h3>
        {labelData.map((label, index) => (
          <div key={index}>{label.label}</div>
        ))}
      </div>
    </div>
  );
};

export default ImageAnnotation;
